DROP TRIGGER IF EXISTS `on_new_order_update_admin_account`;
DELIMITER $$
CREATE TRIGGER `on_new_order_update_admin_account` BEFORE INSERT ON `orders` FOR EACH ROW begin 
	set @current_Balance=(select amount from bank_info where user_id=1);
	set @new_value=(select product_price from products where products.product_id=new.product_id);
    set @new_Balance=@current_Balance+@new_value;
end
$$
DELIMITER ;

